mapper.select:
SELECT platformName,channelName,leadsCnt,leadsUserCnt,actualConsume FROM ( SELECT "易车APP" platformName, "1" platformId, a.channel_name channelName, SUM(leads_cnt) leadsCnt, SUM(leads_user_cnt) leadsUserCnt, SUM(actual_consume) actualConsume FROM yiche_app_app_leads_channel_day_all a,imp_platform_channel b
a.channel_name = #{channelName}
AND bt BETWEEN #{startTime} AND #{endTime} AND a.channel_id=b.channel_id AND b.platform_id='1' GROUP BY a.channel_name
UNION SELECT "报价APP" platformName, "2" platformId, a.channel_name channelName, SUM(leads_cnt) leadsCnt, SUM(leads_user_cnt) leadsUserCnt, SUM(actual_consume) actualConsume FROM yiche_quote_app_leads_channel_day_all a,imp_platform_channel b
a.channel_name = #{channelName}
AND bt BETWEEN #{startTime} AND #{endTime} AND a.channel_id=b.channel_id AND b.platform_id='2' GROUP BY a.channel_name
UNION SELECT "PCWAP" platformName, "3" platformId, a.channel_name channelName, SUM(cluecount) leadsCnt, SUM(clueusercount) leadsUserCnt, SUM(actual_consume) actualConsume FROM yiche_pcwap_app_leads_channel_day_all a,imp_platform_channel b
a.channel_name = #{channelName}
AND dt BETWEEN #{startTime} AND #{endTime} AND a.channel_id=b.channel_id AND b.platform_id='3' GROUP BY a.channel_name
) yiche_channel_consume_sum
platformId=#{platformId}

SELECT platformName,channelName,leadsCnt,leadsUserCnt,actualConsume,bt FROM ( SELECT "易车APP" platformName, "1" platformId, a.channel_name channelName, leads_cnt leadsCnt, leads_user_cnt leadsUserCnt, actual_consume actualConsume, bt bt FROM yiche_app_app_leads_channel_day_all a,imp_platform_channel b
a.channel_name = #{channelName}
AND bt BETWEEN #{startTime} AND #{endTime} AND a.channel_id=b.channel_id AND b.platform_id='1'
UNION SELECT "报价APP" platformName, "2" platformId, a.channel_name channelName, leads_cnt leadsCnt, leads_user_cnt leadsUserCnt, actual_consume actualConsume, bt bt FROM yiche_quote_app_leads_channel_day_all a,imp_platform_channel b
a.channel_name = #{channelName}
AND bt BETWEEN #{startTime} AND #{endTime} AND a.channel_id=b.channel_id AND b.platform_id='2'
UNION SELECT "PCWAP" platformName, "3" platformId, a.channel_name channelName, cluecount leadsCnt, clueusercount leadsUserCnt, actual_consume actualConsume, dt bt FROM yiche_pcwap_app_leads_channel_day_all a,imp_platform_channel b
a.channel_name = #{channelName}
AND dt BETWEEN #{startTime} AND #{endTime} AND a.channel_id=b.channel_id AND b.platform_id='3'
) yiche_channel_consume_detail
platformId=#{platformId}
