mapper.select:
SELECT platformName,channelName,leadsCnt,leadsUserCnt,actualConsume FROM ( SELECT "易车APP" platformName, channel_name channelName, SUM(leads_cnt) leadsCnt, sum(leads_user_cnt) leadsUserCnt, sum(actual_consume) actualConsume FROM yiche_app_app_leads_channel_day_all
channelName = #{channelName}
AND bt BETWEEN #{startTime} AND #{endTime} GROUP BY channel_name
UNION SELECT "报价APP" platformName, channel_name channelName, SUM(leads_cnt) leadsCnt, sum(leads_user_cnt) leadsUserCnt, sum(actual_consume) actualConsume FROM yiche_quote_app_leads_channel_day_all
channelName = #{channelName}
AND bt BETWEEN #{startTime} AND #{endTime} GROUP BY channel_name
UNION SELECT "PCWAP" platformName, channel_name channelName, SUM(cluecount) leadsCnt, sum(clueusercount) leadsUserCnt, sum(actual_consume) actualConsume FROM yiche_pcwap_app_leads_channel_day_all
channelName = #{channelName}
AND dt BETWEEN #{startTime} AND #{endTime} GROUP BY channel_name
) yiche_channel_consume_sum
platformName=#{platformName}

SELECT platformName,channelName,leadsCnt,leadsUserCnt,actualConsume,bt FROM ( SELECT "易车APP" platformName, channel_name channelName, leads_cnt leadsCnt, leads_user_cnt leadsUserCnt, actual_consume actualConsume, bt bt FROM yiche_app_app_leads_channel_day_all
channelName = #{channelName}
AND bt BETWEEN #{startTime} AND #{endTime}
UNION SELECT "报价APP" platformName, channel_name channelName, leads_cnt leadsCnt, leads_user_cnt leadsUserCnt, actual_consume actualConsume, bt bt FROM yiche_quote_app_leads_channel_day_all
channelName = #{channelName}
AND bt BETWEEN #{startTime} AND #{endTime}
UNION SELECT "PCWAP" platformName, channel_name channelName, cluecount leadsCnt, clueusercount leadsUserCnt, actual_consume actualConsume, dt bt FROM yiche_pcwap_app_leads_channel_day_all
channelName = #{channelName}
AND dt BETWEEN #{startTime} AND #{endTime}
) yiche_channel_consume_detail
platformName=#{platformName}
